using Egw.PubManagement.Core.GraphQl;
using Egw.PubManagement.Persistence.Entities;
using Egw.PubManagement.Persistence.Enums;

using HotChocolate.Data;

using WhiteEstate.DocFormat;
namespace Egw.PubManagement.Application.Models;

/// <summary>
/// Mp3 File
/// <see cref="Mp3File"/>
/// </summary>
public class PublicationMp3FileDto : ITimeStampedEntity, IProjectedDto<Mp3File>
{
    /// <summary>
    /// Id
    /// </summary>
    [IsProjected]
    public ParaId ParaId { get; init; }

    /// <summary>
    /// Voice type
    /// </summary>
    public VoiceTypeEnum VoiceType { get; init; }

    /// <summary>
    /// Voice is autogenerated
    /// </summary>
    public bool IsGenerated { get; init; }

    /// <summary>
    /// Publication Id
    /// </summary>
    [IsProjected]
    public int PublicationId { get; init; }

    /// <summary>
    /// Element Id
    /// </summary>
    public int ElementId { get; init; }

    /// <summary>
    /// Mp3 File Name
    /// </summary>
    [IsProjected]
    public string Filename { get; init; } = "";


    /// <inheritdoc />
    public DateTimeOffset CreatedAt { get; init; }

    /// <inheritdoc />
    public DateTimeOffset UpdatedAt { get; init; }
}